import _ from "lodash";
import {
  FETCH_EMPLOYEES,
  FETCH_EMPLOYEE,
  CREATE_EMPLOYEE,
  CALCULATE_SALARY,
} from "../actions/types";

const employeeReducer = (state = {}, action) => {
  switch (action.type) {
    case FETCH_EMPLOYEES:
      return { ...state, ..._.mapKeys(action.payload, "employeeId") };
    case FETCH_EMPLOYEE:
      return state;
    case CREATE_EMPLOYEE:
      return state;
    case CALCULATE_SALARY:
      return state;
    default:
      return state;
  }
};

export default employeeReducer;
