import React from "react";
import { Field, reduxForm } from "redux-form";

const EmployeeAdd = () => {
  const renderError = ({ error, touched }) => {
    if (touched && error) {
      return (
        <div className="ui error message">
          <div className="header">{error}</div>
        </div>
      );
    }
  };

  const renderInput = ({ input, label, meta }) => {
    const className = `field ${meta.error && meta.touched ? "error" : ""}`;
    return (
      <div className={className}>
        <label>{label}</label>
        <input {...input} autoComplete="off" placeholder={label} />
        {renderError(meta)}
      </div>
    );
  };

  return (
    <form className="ui form error">
      <Field name="firstName" component={renderInput} label="First Name" />
      <Field name="lastName" component={renderInput} label="Last Name" />
      <Field name="middleName" component={renderInput} label="Middle Name" />

      <div className="field">
        <label>Middle Name</label>
        <input type="text" name="last-name" placeholder="Last Name" />
      </div>
      <div className="two fields">
        <div className="eleven wide field">
          <label>Birth Date</label>
          <div className="three fields">
            <div className="field">
              <select className="ui fluid dropdown">
                <option value="">Month</option>
              </select>
            </div>
            <div className="field">
              <select className="ui fluid dropdown">
                <option value="">Day</option>
              </select>
            </div>
            <div className="field">
              <select className="ui fluid dropdown">
                <option value="">Year</option>
              </select>
            </div>
          </div>
        </div>
        <div className="five wide field">
          <label>TIN</label>
          <input type="text" name="tin" placeholder="TIN" />
        </div>
      </div>
      <div className="inline fields">
        <label>Employment Type:</label>
        <div className="field">
          <div className="ui radio checkbox">
            {/* <input type="radio" name="fruit" tabIndex="0" className="hidden" /> */}
            <label>Contractual</label>
          </div>
        </div>
        <div className="field">
          <div className="ui radio checkbox">
            {/* <input type="radio" name="fruit" tabIndex="0" className="hidden" /> */}
            <label>Regular</label>
          </div>
        </div>
      </div>
    </form>
  );
};

const validate = (formValues) => {
  const errors = {};

  return errors;
};

export default reduxForm({
  form: "EmployeeAdd",
  validate,
})(EmployeeAdd);
