import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { calculateSalary } from "../../actions";

const EmployeeCalculateSalary = ({ currentEmployeeId }) => {
  const [effectiveDays, setEffectiveDays] = useState("");

  const dispatch = useDispatch();
  const employee = useSelector((state) =>
    currentEmployeeId
      ? state.employees.find((emp) => emp.employeeId === currentEmployeeId)
      : null
  );

  useEffect(() => {
    if (employee)
      setEffectiveDays(
        employee.effectiveDays > 0 ? employee.effectiveDays : ""
      );
  }, [employee]);

  const handleOnChange = (e) => {
    const value = e.target.value;
    if (!isNaN(parseFloat(value)) && isFinite(value)) {
      setEffectiveDays(value);
    }
  };

  return (
    <div className="ui grid">
      <div className="one wide column"></div>
      <div className="fourteen wide column">
        <form className="ui form">
          <div class="ui divided items">
            <div class="item">
              <div class="content">
                <div class="ui large header">Kelvin Ian Manalo</div>
                <div class="meta">Name</div>
              </div>
            </div>
            <div class="item">
              <div class="content">
                <div className="ui two column grid">
                  <div className="column">
                    <div class="ui medium header">Contractual</div>
                    <div class="meta">Employee Type</div>
                  </div>
                  <div className="ui column">
                    <div class="ui medium header">PHP 18,000</div>
                    <div class="meta">Rate per day</div>
                  </div>
                </div>
              </div>
            </div>
            <div class="item">
              <div class="content">
                <div className="ui two column grid">
                  <div className="column">
                    <div class="ui medium header">January 04, 1993</div>
                    <div class="meta">Birth Date</div>
                  </div>
                  <div className="ui column">
                    <div class="ui medium header">12312312321</div>
                    <div class="meta">TIN</div>
                  </div>
                </div>
              </div>
            </div>
            <div class="item">
              <div class="content">
                <div className="ui two column grid">
                  <div className="column">
                    <input
                      type="text"
                      placeholder="Work Days"
                      value={effectiveDays}
                      onChange={handleOnChange}
                    ></input>
                  </div>
                  <div className="ui column">
                    <div className="ui violet labeled icon button">
                      <i className="calculator icon"></i>
                      Calculate
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div class="item">
              <div class="content">
                <div class="ui large header">PHP 32,000</div>
                <div class="meta">Computed Salary</div>
              </div>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EmployeeCalculateSalary;
