import React from "react";
import { Field, reduxForm } from "redux-form";
import { connect } from "react-redux";
import { createEmployee } from "../../actions";
import moment from "moment";
import numeral from "numeral";

class EmployeeAdd extends React.Component {
  onSubmit = (formValues) => {
    console.log(formValues);
  };

  renderError({ error, touched }) {
    if (touched && error) {
      return (
        <div className="ui error message">
          <div className="content">{error}</div>
        </div>
      );
    }
  }

  renderInput = ({ input, label, meta, placeholder }) => {
    const className = `field ${meta.error && meta.touched ? "error" : ""}`;
    return (
      <div className={className}>
        <label>{label}</label>
        <input {...input} placeholder={placeholder} />
        {this.renderError(meta)}
      </div>
    );
  };

  birthDateMasking = (val, prevVal) => {
    // Prevent non-digit characters being entered
    if (isNaN(parseInt(val[val.length - 1], 10))) {
      return val.slice(0, -1);
    }

    // When user is deleting, this prevents immediate re-addition of '/' when it's deleted
    if (prevVal && prevVal.length >= val.length) {
      return val;
    }

    // Add / at appropriate sections of the input
    if (val.length === 2 || val.length === 5) {
      val += "/";
    }

    // Prevent characters being entered after Dob is full
    if (val.length >= 10) {
      return val.slice(0, 10);
    }

    return val;
  };

  formatAmount = (input) => {
    if (isNaN(parseInt(input[input.length - 1], 10))) {
      return input.slice(0, -1);
    }
    if (!input) return;

    return input.replace(/,/g, "").replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };

  normalizeAmount = (val) => {
    return val.replace(/,/g, "");
  };

  render() {
    return (
      <form
        onSubmit={this.props.handleSubmit(this.onSubmit)}
        className="ui form error"
        autoComplete="off"
      >
        <Field
          name="firstName"
          component={this.renderInput}
          label="First Name"
          placeholder="First Name"
        />
        <Field
          name="middleName"
          component={this.renderInput}
          label="Middle Name"
          placeholder="Middle Name"
        />
        <Field
          name="lastName"
          component={this.renderInput}
          label="Last Name"
          placeholder="Last Name"
        />

        <div className="two fields">
          <div className="six wide field">
            <Field
              name="birthDate"
              component={this.renderInput}
              label="Birth Date"
              placeholder="MM/DD/YYYY"
              normalize={this.birthDateMasking}
            />
          </div>
          <div className="five wide field">
            <Field
              name="baseSalary"
              component={this.renderInput}
              label="Base Salary"
              placeholder="Base Salary"
              format={this.formatAmount}
            />
          </div>
          <div className="five wide field">
            <Field
              name="tin"
              component={this.renderInput}
              label="TIN"
              placeholder="TIN"
            />
          </div>
        </div>
        <div className="inline fields">
          <label>Employmee Type:</label>
          <div className="field">
            <Field
              name="employeeType"
              component="input"
              type="radio"
              value="1"
            />
            <label>Contractual</label>
          </div>
          <div className="field">
            <Field
              name="employeeType"
              component="input"
              type="radio"
              value="2"
            />
            <label>Regular</label>
          </div>
        </div>
        <div className="extra content">
          <button className="ui floated button violet">Submit</button>
        </div>
      </form>
    );
  }
}

const validate = (formValues) => {
  const errors = {};

  if (!formValues.firstName) {
    errors.firstName = "You must enter a first name";
  }

  if (!formValues.lastName) {
    errors.lastName = "You must enter a last name";
  }

  if (!formValues.middleName) {
    errors.middleName = "You must enter a middle name";
  }

  const validDate = moment(formValues.birthDate, "MM/DD/YYYY", true).isValid();

  if (validDate) {
    if (new Date(formValues.birthDate) > new Date()) {
      errors.birthDate = "Cannot be future dated";
    }
  } else {
    errors.birthDate = "Invalid date";
  }

  if (!formValues.employeeType) {
    errors.employeeType = "You must enter an employee type";
  }

  if (!formValues.baseSalary) {
  }

  return errors;
};

const wrapComponent = reduxForm({
  form: "EmployeeAdd",
  validate,
})(EmployeeAdd);

//connect

export default connect(null, { createEmployee })(wrapComponent);
