import React from "react";
import { Field, reduxForm } from "redux-form";

class EmployeeAdd extends React.Component {
  renderError({ error, touched }) {
    if (touched && error) {
      return (
        <div className="ui error message">
          <div className="header">{error}</div>
        </div>
      );
    }
  }

  renderInput = ({ input, label, meta }) => {
    const className = `field ${meta.error && meta.touched ? "error" : ""}`;
    return (
      <div className={className}>
        <label>{label}</label>
        <input {...input} placeholder={label} />
        {this.renderError(meta)}
      </div>
    );
  };

  renderDropDown = ({ input, label, meta }) => {
    const className = `field ${meta.error && meta.touched ? "error" : ""}`;
    return (
      <div className={className}>
        <label>{label}</label>
        <input {...input} placeholder={label} />
        {this.renderError(meta)}

        <div className="field">
          <select className="ui fluid dropdown">
            <option value="">Month</option>
          </select>
        </div>
      </div>
    );
  };

  render() {
    return (
      <form className="ui form error" autoComplete="off">
        <Field
          name="firstName"
          component={this.renderInput}
          label="First Name"
        />
        <Field name="lastName" component={this.renderInput} label="Last Name" />
        <Field
          name="middleName"
          component={this.renderInput}
          label="Middle Name"
        />

        <div className="two fields">
          <div className="eleven wide field">
            <label>Birth Date</label>
            <div className="three fields">
              <div className="field">
                <select className="ui fluid dropdown">
                  <option value="">Month</option>
                </select>
              </div>
              <div className="field">
                <select className="ui fluid dropdown">
                  <option value="">Day</option>
                </select>
              </div>
              <div className="field">
                <select className="ui fluid dropdown">
                  <option value="">Year</option>
                </select>
              </div>
            </div>
          </div>
          <div className="five wide field">
            <label>TIN</label>
            <input type="text" name="tin" placeholder="TIN" />
          </div>
        </div>
        <div className="inline fields">
          <label>Employment Type:</label>
          <div className="field">
            <div className="ui radio checkbox">
              {/* <input type="radio" name="fruit" tabIndex="0" className="hidden" /> */}
              <label>Contractual</label>
            </div>
          </div>
          <div className="field">
            <div className="ui radio checkbox">
              {/* <input type="radio" name="fruit" tabIndex="0" className="hidden" /> */}
              <label>Regular</label>
            </div>
          </div>
        </div>
      </form>
    );
  }
}

const validate = (formValues) => {
  const errors = {};

  if (!formValues.firstName) {
    errors.firstName = "You must enter a first name";
  }

  if (!formValues.lastName) {
    errors.lastName = "You must enter a last name";
  }

  if (!formValues.middleName) {
    errors.middleName = "You must enter a middle name";
  }

  return errors;
};

export default reduxForm({
  form: "EmployeeAdd",
  validate,
})(EmployeeAdd);
