import React from "react";
import { useSelector } from "react-redux";
import { EMPLOYEE_TYPE } from "../../Constants/constants";

const EmployeeList = () => {
  const employees = useSelector((state) => {
    return state.employees;
  });

  if (!employees.length) return <div>Loading...</div>;

  const renderRows = () => {
    const renderStatus = (effectiveDays) => {
      if (effectiveDays > 0)
        return (
          <>
            <i className="icon checkmark"></i> "Completed"
          </>
        );
      else
        return (
          <>
            <i className="icon ellipsis horizontal"></i> Pending
          </>
        );
    };

    return employees.map(({ name, employeeType, effectiveDays }) => {
      console.log(employeeType);
      return (
        <tr className={effectiveDays > 0 ? "positive" : ""}>
          <td>{name}</td>
          <td>
            <i
              className={
                employeeType === 1 ? "icon outline user" : "icon outline"
              }
            ></i>
          </td>
          <td>{renderStatus(effectiveDays)}</td>
        </tr>
      );
    });
  };

  return (
    <div className="nine wide column">
      <div className="ui container">
        <table className="ui celled selectable table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Employee Type</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>{renderRows()}</tbody>
          <tfoot className="full-width">
            <tr className="right aligned">
              <th colSpan="3">
                <div className="extra content">
                  <button className="ui violet labeled icon button">
                    <i className="user icon"></i> Add Employee
                  </button>
                </div>
              </th>
            </tr>
          </tfoot>
        </table>
      </div>
    </div>
  );
};

export default EmployeeList;
